.call {
  display: none;

  @media screen and(min-width: 768px) {
    font-weight: bold;
    font-size: 12px;
    line-height: 15px;
    letter-spacing: 0.08em;
    text-transform: uppercase;

    color: rgba(255, 107, 9, 1);
    border: 2px solid rgba(255, 107, 9, 1);

    display: flex;
    justify-content: center;
    align-items: center;
    width: 260px;
    height: 48px;

    margin-right: 40px;
    margin-left: auto;
  }

  @media screen and (min-width: 1200px) {
    font-size: 14px;
    line-height: 17px;
    letter-spacing: 0.14em;

    margin-left: 60px;
    margin-right: 0px;
    width: 336px;
    height: 70px;
  }
}

.menu-toggle {
  display: block;
  margin-left: auto;
  align-items: center;
  background-color: black;

  @media screen and(min-width: 768px) {
    margin-left: 0px;
  }

  @media screen and(min-width: 1200px) {
    display: none;
  }
}

.menu-toggle:focus,
.menu-toggle:hover {
  fill: rgba(255, 107, 9, 1);
  outline: none;
  transition: 250ms cubic-bezier(0.4, 0, 0.2, 1);
}

.menu-toggle > .menu-toggle__icon:focus,
.menu-toggle > .menu-toggle__icon:hover {
  fill: rgba(255, 107, 9, 1);
  transition: 250ms cubic-bezier(0.4, 0, 0.2, 1);
}

